// Análisis ML de performance y optimización automática
const performanceData = $input.item.json;

// Extraer métricas clave
const metrics = {
  cartAbandonmentRate: performanceData.cartAbandonmentRate || 0,
  emailOpenRate: performanceData.emailOpenRate || 0,
  emailClickRate: performanceData.emailClickRate || 0,
  conversionRate: performanceData.conversionRate || 0,
  recoveryRate: performanceData.recoveryRate || 0,
  averageOrderValue: performanceData.averageOrderValue || 0,
  revenue: performanceData.revenue || 0
};

// Análisis de tendencias (comparar con semana anterior)
const previousWeek = performanceData.previousWeek || {};
const trends = {
  cartAbandonment: metrics.cartAbandonmentRate - (previousWeek.cartAbandonmentRate || 0),
  emailOpen: metrics.emailOpenRate - (previousWeek.emailOpenRate || 0),
  emailClick: metrics.emailClickRate - (previousWeek.emailClickRate || 0),
  conversion: metrics.conversionRate - (previousWeek.conversionRate || 0),
  recovery: metrics.recoveryRate - (previousWeek.recoveryRate || 0)
};

// Detectar problemas
const issues = [];
if (metrics.cartAbandonmentRate > 80) {
  issues.push({
    type: 'high_abandonment',
    severity: 'critical',
    message: 'Tasa de abandono muy alta',
    recommendation: 'Revisar proceso de checkout'
  });
}

if (metrics.emailOpenRate < 20) {
  issues.push({
    type: 'low_open_rate',
    severity: 'warning',
    message: 'Tasa de apertura baja',
    recommendation: 'Optimizar asuntos y timing'
  });
}

if (metrics.emailClickRate < 5) {
  issues.push({
    type: 'low_click_rate',
    severity: 'warning',
    message: 'Tasa de clic baja',
    recommendation: 'Mejorar CTAs y contenido'
  });
}

// Calcular optimizaciones sugeridas
const optimizations = [];

// Optimización de timing
if (trends.emailOpen < -5) {
  optimizations.push({
    type: 'timing',
    action: 'adjust_send_times',
    description: 'Ajustar horarios de envío basado en aperturas',
    priority: 'high'
  });
}

// Optimización de mensajes
if (trends.emailClick < -2) {
  optimizations.push({
    type: 'content',
    action: 'test_new_ctas',
    description: 'Probar nuevos CTAs y mensajes',
    priority: 'high'
  });
}

// Optimización de descuentos
if (trends.recovery < -5) {
  optimizations.push({
    type: 'discount',
    action: 'adjust_discounts',
    description: 'Ajustar niveles de descuento',
    priority: 'medium'
  });
}

// Calcular score de salud general (0-100)
let healthScore = 100;
if (metrics.cartAbandonmentRate > 75) healthScore -= 20;
if (metrics.emailOpenRate < 25) healthScore -= 15;
if (metrics.emailClickRate < 8) healthScore -= 15;
if (metrics.conversionRate < 15) healthScore -= 20;
if (metrics.recoveryRate < 30) healthScore -= 15;
if (trends.cartAbandonment > 5) healthScore -= 10;
if (trends.emailOpen < -5) healthScore -= 5;

healthScore = Math.max(0, Math.min(100, healthScore));

// Generar recomendaciones ML
const mlRecommendations = [];

if (healthScore < 70) {
  mlRecommendations.push({
    priority: 'critical',
    action: 'comprehensive_review',
    description: 'Revisión completa del sistema recomendada'
  });
}

if (metrics.recoveryRate < 25 && metrics.cartAbandonmentRate > 60) {
  mlRecommendations.push({
    priority: 'high',
    action: 'improve_recovery_sequence',
    description: 'Mejorar secuencia de recuperación'
  });
}

return [{
  json: {
    timestamp: new Date().toISOString(),
    period: '7d',
    metrics: metrics,
    trends: trends,
    issues: issues,
    optimizations: optimizations,
    mlRecommendations: mlRecommendations,
    healthScore: healthScore,
    healthStatus: healthScore > 80 ? 'excellent' : healthScore > 60 ? 'good' : healthScore > 40 ? 'fair' : 'poor'
  }
}];

// Aplicar optimizaciones automáticas
const analysis = $input.item.json;

const autoOptimizations = [];

// Ajustar timing si open rate bajo
if (analysis.metrics.emailOpenRate < 25) {
  autoOptimizations.push({
    type: 'timing',
    action: 'shift_send_times',
    shiftHours: 2,
    reason: 'Low open rate'
  });
}

// Ajustar descuentos si recovery bajo
if (analysis.metrics.recoveryRate < 30) {
  autoOptimizations.push({
    type: 'discount',
    action: 'increase_discounts',
    increasePercent: 5,
    reason: 'Low recovery rate'
  });
}

// Ajustar frecuencia si bounce alto
if (analysis.metrics.bounceRate > 5) {
  autoOptimizations.push({
    type: 'frequency',
    action: 'reduce_frequency',
    reducePercent: 20,
    reason: 'High bounce rate'
  });
}

return [{
  json: {
    ...analysis,
    autoOptimizations: autoOptimizations,
    appliedAt: new Date().toISOString()
  }
}];
